mapper.select:
SELECT T.TODO_NO , T.MEMBER_NO , T.PROJECT_NO , (SELECT P.PROJECT_NAME FROM PROJECT P WHERE P.PROJECT_NO = T.PROJECT_NO) AS PROJECT_NAME , T.TODO_CONTENT , T.TODO_STATUS , T.TODO_REGDATE , T.TODO_STRDATE , T.TODO_ENDDATE , T.TODO_DONEDATE FROM TODO T WHERE T.MEMBER_NO = #{memberNo}
AND T.PROJECT_NO = #{projectNo}
AND T.PROJECT_NO = 0
ORDER BY T.TODO_STATUS ASC , T.TODO_ENDDATE ASC , T.TODO_NO DESC

mapper.insert:
INSERT INTO TODO (
	TODO_NO
	, MEMBER_NO
	, PROJECT_NO
	, TODO_CONTENT
	, TODO_STATUS
	, TODO_REGDATE
	, TODO_STRDATE
	, TODO_ENDDATE
) VALUES (
	SEQ_TODO.NEXTVAL
	, #{memberNo}
	, #{projectNo}
	, #{todoContent}
	, 'N'
	, CURRENT_TIMESTAMP
	, #{todoStrdate}
	, #{todoEnddate}
)

mapper.update:
UPDATE TODO
SET
	TODO_STATUS = #{todoStatus}
	, TODO_DONEDATE = CASE WHEN #{todoStatus} = 'Y' THEN CURRENT_TIMESTAMP 
					  ELSE NULL 
					  END
WHERE TODO_NO = #{todoNo}
  AND MEMBER_NO = #{memberNo}

UPDATE TODO
SET TODO_CONTENT = #{todoContent}
WHERE TODO_NO = #{todoNo}
  AND MEMBER_NO = #{memberNo}
